attrs.script:
# Set creds and headers
era_user = '@@{era_creds.username}@@'
era_pass = '@@{era_creds.secret}@@'
headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}

# Get Time Machine Capability
url  = "https://@@{era_ip}@@:8443/era/v0.8/tms/@@{TM_ID}@@/clones/input-file?category=database%3Bvm_info"
resp = urlreq(url, verb='GET', auth='BASIC', user=era_user, passwd=era_pass, headers=headers)

if resp.ok:

  # Cycle through the various properties
  for property in json.loads(resp.content)['properties']:

    # Set the various variables
    if property['name'] == "vm_name":
      print "VM_NAME={0}".format(property['default_value'])
    elif property['name'] == "working_dir":
      print "WORKING_DIR={0}".format(property['default_value'])
    elif property['name'] == "era_deploy_base":
      print "ERA_BASE={0}".format(property['default_value'])

# In the event something went wrong with the API call
else:
  print "Get Input File request failed", json.dumps(json.loads(resp.content), indent=4)
  exit(1)

# Set creds and headers
era_user = '@@{era_creds.username}@@'
era_pass = '@@{era_creds.secret}@@'
headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}

# Get Time Machine Capability
url  = "https://@@{era_ip}@@:8443/era/v0.9/dbservers/@@{SOURCE_DBSERVER_ID}@@?value-type=id&load-dbserver-cluster=false&load-databases=true&load-clones=false&load-metrics=false&detailed=true&curator=false&time-zone=UTC"
resp = urlreq(url, verb='GET', auth='BASIC', user=era_user, passwd=era_pass, headers=headers)

if resp.ok:

  # Cycle through the various properties
  for property in json.loads(resp.content)['properties']:

    # Set the various variables
    if property['name'] == "compute_profile_id":
      print "COMPUTE_PROFILE_ID={0}".format(property['value'])
    elif property['name'] == "network_profile_id":
      print "NETWORK_PROFILE_ID={0}".format(property['value'])
    elif property['name'] == "db_parameter_profile_id":
      print "DB_PROFILE_ID={0}".format(property['value'])

  for property in json.loads(resp.content)['databases'][0]['properties']:
    # Set the various variables
    if property['name'] == "db_parameter_profile_id":
      print "DB_PROFILE_ID={0}".format(property['value'])

# In the event something went wrong with the API call
else:
  print "Get Input File request failed", json.dumps(json.loads(resp.content), indent=4)
  exit(1)

# Set creds and headers
era_user = '@@{era_creds.username}@@'
era_pass = '@@{era_creds.secret}@@'
headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}

# Set the Clone DB Name
if "@@{cloned_db_name}@@" == "":
  cloneName = '@@{source_db_name}@@_Clone_@@{calm_time("%Y%m%d%H%M")}@@'
else:
  cloneName = "@@{cloned_db_name}@@"

# Set the URL and payload
url     = "https://@@{era_ip}@@:8443/era/v0.8/tms/@@{TM_ID}@@/clones"
payload = {
  "snapshotId": "@@{VALIDATED_SNAP_ID}@@",
  "cloneDescription": "Clone of '@@{source_db_name}@@' managed through Calm application @@{calm_application_name}@@",
  "timeZone": "UTC",
  "cloneName": cloneName,
  "cloneInfo": [
    {
      "name": "db_password",
      "value": "@@{cloned_db_password}@@"
    },
    {
      "name": "vm_name",
      "value": "@@{VM_NAME}@@"
    },
    {
      "name": "client_public_key",
      "value": "@@{db_server_creds.public_key}@@"
    },
    {
      "name": "working_dir",
      "value": "@@{WORKING_DIR}@@"
    },
    {
      "name": "era_deploy_base",
      "value": "@@{ERA_BASE}@@"
    },
    {
      "name": "create_dbserver",
      "value": True
    },
    {
      "name": "compute_profile_id",
      "value": "@@{COMPUTE_PROFILE_ID}@@"
    },
    {
      "name": "network_profile_id",
      "value": "@@{NETWORK_PROFILE_ID}@@"
    },
    {
      "name": "db_parameter_profile_id",
      "value": "@@{DB_PROFILE_ID}@@"
    }
  ],
  "timeMachineId": "@@{TM_ID}@@",
  "latestSnapshot": False
}

# Make the call and set the response operation ID to the variable
resp = urlreq(url, verb='POST', auth='BASIC', user=era_user, passwd=era_pass, params=json.dumps(payload), headers=headers)
if resp.ok:
  print "CLONE_OPERATION_ID={0}".format(json.loads(resp.content)['operationId'])
else:
  print "Post Database clone request failed", json.dumps(json.loads(resp.content), indent=4)
  exit(1)

# Set creds and headers
era_user = '@@{era_creds.username}@@'
era_pass = '@@{era_creds.secret}@@'
headers = {'Content-Type': 'application/json', 'Accept': 'application/json'}

# Set the URL and payload
url     = "https://@@{era_ip}@@:8443/era/v0.8/clones/@@{CLONE_ID}@@/refresh"
payload = {
  "cloneId": "@@{CLONE_ID}@@",
  "snapshotId": "@@{VALIDATED_SNAP_ID}@@",
  "latestSnapshot": False,
  "timeZone": "UTC",
  "cloneInfo": [
    {
      "name": "working_dir",
      "value": "@@{WORKING_DIR}@@"
    }
  ]
}

# Make the call and set the response operation ID to the variable
resp = urlreq(url, verb='POST', auth='BASIC', user=era_user, passwd=era_pass, params=json.dumps(payload), headers=headers)
if resp.ok:
  print "REFRESH_OPERATION_ID={0}".format(json.loads(resp.content)['operationId'])
else:
  print "Post Database refresh request failed", json.dumps(json.loads(resp.content), indent=4)
  exit(1)
